import type {Locator} from '@clayui/shared';

export type Item = {
	[propName: string]: any;

	/**
	 * Label to show.
	 */
	label?: string;

	/**
	 * Global unique id of the item list. Generated by the component.
	 */
	key?: string;

	/**
	 * Hidden value of the item.
	 */
	value?: string;
};

export type Locators = {
	id?: Locator;
	label: Locator;
	value: Locator;
};

export type LastChangeLiveRegion = {
	label: string;
	action: 'removed' | 'added';
};
